import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const ChangePassword = () => {
  const [old, setOld] = useState("");
  const [disabled , setDisabled] = useState(true);
  const [userData, setUserData] = useState({});
  const callProfile = async ()=>{
    try{
      const res = await fetch('/profile',{
        method: 'GET',
        headers : {
          "Accept": 'application/json',
          "ContentType": 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
  
      setUserData(data);
      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      console.log(err);
      
    }
  }

  useEffect(() => {
    callProfile(); 
  },[]);

  const email = userData.email;
  console.log(email);
  const verifyOld = async(e) =>{
    e.preventDefault();
    const res = await fetch("/verify-old-password",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({old,email})
    });
    const data = await res.json();
    if(res.status === 400 || !data ){
      window.alert(data.error);
      setDisabled(true);
      console.log("Some error in old password verify !");
    }else{ 
      window.alert(data.message);
      setDisabled(false);
    }
  }

  return (
    <>
          <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Change your Password</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="old">Old Password<span className="required-field">*</span></label>
                          <input type="password" value={old} onChange={(e)=>setOld(e.target.value)} id="old" className="form-control" name="old" required/>
                      </div>
                      <button type="submit" name="verifyOld" id="verifyOld" onClick={verifyOld} className="mt-3 btn btn-sm w-100 btn-outline-dark">Verify Old Password</button>
                      <div className="form-group mt-3 mb-2">
                          <label htmlFor="new">New Password<span className="required-field">*</span></label>
                          <input type="password" id="new" disabled={disabled} className="form-control" name="new" required/>
                      </div>
                      <div className="form-group mb-2">
                          <label htmlFor="confirm">Confirm New Password<span className="required-field">*</span></label>
                          <input type="password" id="confirm" disabled={disabled}  className="form-control" name="confirm" required/>
                      </div>     
                    </form>
                    <button type="submit" name="login" disabled id="login"  className="mt-3 btn btn-sm w-100 btn-outline-dark">Continue</button>
                  </div>
                </div>
            </div>
        </div>
    </>
  );
};

export default ChangePassword;
