import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({});
  const callProfile = async ()=>{
    try{
      const res = await fetch('/profile',{
        method: 'GET',
        headers : {
          "Accept": 'application/json',
          "ContentType": 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
      setUserData(data);
      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      console.log(err);
      navigate('/login');
    }
  }

  useEffect(() => {
    callProfile(); 
  },[]);

  return (
    <div>
       <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Ecovian Profile</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="GET">
                      <div className="form-group mb-2">
                          <label htmlFor="email">Email<span className="required-field">*</span></label>
                          <input type="text" id="email"  className="form-control" placeholder="Enter Email" name="email" required/>
                      </div>  
                          
                      <div className="form-group">
                          <label htmlFor="password">Password<span className="required-field">*</span></label>
                          <input type="password" id="password"  className="form-control" placeholder="Enter Password" name="password" required/> 
                      </div>   
                    </form>
                    <button type="submit" name="login" id="login" className="mt-3 btn btn-sm w-100 btn-outline-dark">Login</button>
                  </div>
                  <ul class="list-group list-group-flush">
                    <li class="list-group-item">{userData.name}</li>
                    <li class="list-group-item">{userData.email}</li>
                    <li class="list-group-item">{userData.phone}</li>
                  </ul>
                  <div className="card-footer text-muted">
                  Don't have an account? <NavLink to="/register">Register Now</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </div>
  );
};

export default Profile;
