import React, {useState} from "react";
import { NavLink, useNavigate } from "react-router-dom";

const AddInventory = () => {

  const navigate = useNavigate();
  const [inventory, setInventory] = useState({
    name : "",
    address : "",
  });
  
  let name,value;


  const handleInputs =(e)=>{
    console.log(e); 
    name = e.target.name;
    value = e.target.value;

    setInventory({
      ...inventory,[name] : value
    });
  }

  const postInventory = async(e) =>{
    e.preventDefault();
    const {name,address} = inventory;
    const res = await fetch("/add-inventory",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({name,address})
    });
    const data = await res.json();
    
    if(res.status === 422 || !data ){
      window.alert(data.error);
      console.log("Please enter all the fields !");
    }else{
      window.alert(data.message);
      console.log("Created Successfully !");
      navigate("/profile");
    }
  }

  return (
    <>
          <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Create new Inventory Store</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="name">Store Name<span className="required-field">*</span></label>
                          <input type="text" id="name" values = {inventory.name} onChange={handleInputs} className="form-control" placeholder="Enter Store Name" name="name" required/>
                      </div>      
                      <div className="form-group mb-2">
                          <label htmlFor="address">Store Address<span className="required-field">*</span></label>
                          <input type="text" id="address" values = {inventory.address} onChange={handleInputs} className="form-control" placeholder="Enter Store Address" name="address" required/>
                      </div>     
                    </form>
                    <button type="submit" name="addInventory" id="addInventory" onClick={postInventory} className="mt-3 btn btn-sm w-100 btn-outline-dark">Add Store</button>
                  </div>
                  <div className="card-footer text-muted">
                  List All Stores <NavLink to="/">See List</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </>
  );
};

export default AddInventory;
