import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {useContext} from "react";
import { NavLink,  } from "react-router-dom";
import {UserContext} from "../App";

const GenerateToken = () => {
  const {state, dispatch} = useContext(UserContext);


  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const loginUser = async(e) =>{
    e.preventDefault();
    const res = await fetch("/signin",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({email,password})
    });
    const data = await res.json();
    if(res.status === 400 || !data ){
      window.alert(data.error);
      console.log("Some error in Login !");
    }else{ 
      window.alert(data.message);
      dispatch({type: "USER", payload: true});
      console.log("Login Successfull!");
      navigate("/");
    }
  }

  const navigate = useNavigate();
  const [userData, setUserData] = useState({});
  const callProfile = async ()=>{
    try{
      const res = await fetch('/profile',{
        method: 'GET',
        headers : {
          "Accept": 'application/json',
          "ContentType": 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
  
      setUserData(data);
      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      console.log(err);
      navigate('/login');
    }
  }

  useEffect(() => {
    callProfile(); 
  },[]);

  return (
    <div>
       <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Generate Token</h5>
                  <div className="card-body d-flex flex-column">
                  To reward employees tokens are generated.
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="email">Enter Employee id <span className="required-field">*</span></label>
                          <input type="text" id="email" value={email} onChange={(e)=>setEmail(e.target.value)} className="form-control" placeholder="Enter Email" name="email" required/>
                      </div>      
                      <div className="form-group">
                          <label htmlFor="password">Password<span className="required-field">*</span></label>
                          <input type="password" id="password" value={password} onChange={(e)=>setPassword(e.target.value)}  className="form-control" placeholder="Enter Password" name="password" required/> 
                      </div>   
                    </form>
                    <button type="submit" name="login" id="login" onClick={loginUser} className="mt-3 btn btn-sm w-100 btn-outline-dark">Login</button>
                    <button type="submit" name="login" id="login" onClick={loginUser} className="mt-3 btn btn-sm w-100 btn-outline-dark">Upload bulk data</button>
                  </div>
                  <div className="card-footer text-muted">
                 Facing Issue?  <NavLink to="/register">Submit Feedback</NavLink>
                      </div>
                  
                </div>
            </div>
        </div>
    </div>
  );
};

export default GenerateToken;
