import React, {useState} from "react";
import { NavLink, useNavigate } from "react-router-dom";

const Register = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({
    name : "",
    email : "",
    phone: "",
    password: "",
    cpassword: ""
  });
  
  let name,value;


  const handleInputs =(e)=>{
    console.log(e); 
    name = e.target.name;
    value = e.target.value;

    setUser({
      ...user,[name] : value
    });
  }

  const postData = async(e) =>{
    e.preventDefault();
    const {name,email,password,cpassword,phone} = user;
    const res = await fetch("/register",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({name,email,password,cpassword,phone})
    });
    const data = await res.json();
    if(res.status === 422 || !data ){
      window.alert(data.error);
      console.log("Please enter all the fields !");
    }else{
      window.alert(data.message);
      console.log("Registration Successfull !");
      navigate("/login");
    }
  }

  return (
    <>
    <div className="d-flex justify-content-center align-items-center mt-3">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Register for Ecovia Store</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST" id="register-form">
                      <div className="form-group mb-2">
                          <label htmlFor="name">Full Name<span className="required-field">*</span></label>
                          <input type="text" id="name" className="form-control" placeholder="Full Name" name="name" autoComplete="off"  values = {user.name} onChange={handleInputs} required/>
                      </div> 
                      <div className="form-group mb-2">
                          <label htmlFor="email">Email<span className="required-field">*</span></label>
                          <input type="text" id="email" className="form-control" placeholder="Enter Email" name="email" autoComplete="off" values = {user.email} onChange={handleInputs}  required/>
                      </div>  
                      <div className="form-row">
                      <div className="form-group mb-2">
                          <label htmlFor="password">Password<span className="required-field">*</span></label>
                          <input type="password" id="password" className="form-control" placeholder="Enter Password" name="password" autoComplete="off"  values = {user.password} onChange={handleInputs} required/> 
                      </div>   
                      
                      <div className="form-group">
                        <label htmlFor="cpassword">Repeat Password<span className="required-field">*</span></label>
                        <input type="password" id="cpassword" className="form-control" placeholder="Repeat Password" name="cpassword" autoComplete="off"  values = {user.cpassword} onChange={handleInputs} required/>
                      </div>
                      </div>    
                      
                      <div className="form-group mb-2">
                          <label htmlFor="phone">Phone<span className="required-field">*</span></label>
                          <input type="tel" id="phone" className="form-control" placeholder="Enter Phone" name="phone" autoComplete="off"  values = {user.phone} onChange={handleInputs} required/> 
                      </div> 
                    </form>
                    <button type="submit" name="register" onClick={postData} className="mt-3 btn btn-sm w-100 btn-outline-dark">Register</button>
                  </div>
                  <div className="card-footer text-muted">
                  Already have an account? <NavLink to="/login">Login</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </>
  );
};

export default Register;
