import React, {useState} from "react";
import { NavLink, useNavigate } from "react-router-dom";
import {StarRatingInput, StarRating} from 'react-star-rating-input';

const Feedback = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState({
    name : "",
    email : "",
    phone: "",
    password: "",
    cpassword: ""
  });
  
  let name,value;

  const handleInputs =(e)=>{
    console.log(e); 
    name = e.target.name;
    value = e.target.value;

    setUser({
      ...user,[name] : value
    });
  }

  const postData = async(e) =>{
    e.preventDefault();
    const {name,email,password,cpassword,phone} = user;
    const res = await fetch("/register",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({name,email,password,cpassword,phone})
    });
    const data = await res.json();
    if(res.status === 422 || !data ){
      window.alert(data.error);
      console.log("Please enter all the fields !");
    }else{
      window.alert(data.message);
      console.log("Registration Successfull !");
      navigate("/login");
    }
  }

  
  return (
    <>
    <div className="d-flex justify-content-center align-items-center mt-3">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Send us Feedback (anonymously, yes :)</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST" id="register-form">
                      <div className="form-group mb-2">
                          <label htmlFor="name">Describe your Feedback<span className="required-field">*</span></label>
                          <textarea class="form-control" id="name" rows="3" placeholder="Describe your experience with us! "></textarea>
                      </div> 
                     
                  
                      <div className="form-group">
                        <label htmlFor="cpassword">Rate your experience<span className="required-field">*</span></label>
                        <input type="password" id="cpassword" className="form-control" placeholder="Repeat Password" name="cpassword" autoComplete="off"  values = {user.cpassword} onChange={handleInputs} required/>
                      </div>
                       
                      
                      <div className="form-group mb-2">
                          <label htmlFor="phone">Phone<span className="required-field">*</span></label>
                          <input type="tel" id="phone" className="form-control" placeholder="Enter Phone" name="phone" autoComplete="off"  values = {user.phone} onChange={handleInputs} required/> 
                      </div> 
                    </form>
                    <button type="submit" name="register" onClick={postData} className="mt-3 btn btn-sm w-100 btn-outline-dark">Register</button>
                  </div>
                  <div className="card-footer text-muted">
                  Already have an account? <NavLink to="/login">Login</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </>
  );
  
};

export default Feedback;
