import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({});
  const callProfile = async ()=>{
    try{
      const res = await fetch('/profile',{
        method: 'GET',
        headers : {
          "Accept": 'application/json',
          "ContentType": 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
  
      setUserData(data);
      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      console.log(err);
      navigate('/login');
    }
  }

  useEffect(() => {
    callProfile(); 
  },[]);

  return (
    <div>
       <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Ecovian Profile</h5>
                  <ul class="list-group list-group-flush">
                    <li class="list-group-item">{userData.name}</li>
                    <li class="list-group-item">{userData.email}</li>
                    <li class="list-group-item">{userData.phone}</li>
                  </ul>
                  <div className="col card-footer text-muted ms-auto">
                   <NavLink className="px-5" to="/edit-profile">Edit Profile</NavLink>
                   <NavLink className="px-5" to="/edit-profile">Change Password</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </div>
  );
};

export default Profile;
