import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {useContext} from "react";
import { NavLink,  } from "react-router-dom";
import {UserContext} from "../App";

const GenerateToken = () => {
  const {state, dispatch} = useContext(UserContext);
  const [emp_id, setemp_id] = useState("");
  const [password, setPassword] = useState("");

  const loginUser = async(e) =>{
    e.preventDefault();
    const res = await fetch("/signin",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({emp_id,password})
    });
    const data = await res.json();
    if(res.status === 400 || !data ){
      window.alert(data.error);
      console.log("Some error in Login !");
    }else{ 
      window.alert(data.message);
      dispatch({type: "USER", payload: true});
      console.log("Login Successfull!");
      navigate("/");
    }
  }

  const navigate = useNavigate();
  const [userData, setUserData] = useState({});
  const callProfile = async ()=>{
    try{
      const res = await fetch('/profile',{
        method: 'GET',
        headers : {
          "Accept": 'application/json',
          "ContentType": 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
  
      setUserData(data);
      if(!res.status === 200){
        const error = new Error(res.error);
        throw error;
      }

    }catch(err){
      console.log(err);
      navigate('/login');
    }
  }



  return (
    <div>
       <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Generate Token</h5>
                  <div className="card-body d-flex flex-column">
                  To reward employees tokens are generated.
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="emp_id">Enter Employee id <span className="required-field">*</span></label>
                          <input type="text" id="emp_id" value={emp_id} onChange={(e)=>setemp_id(e.target.value)} className="form-control" placeholder="Enter Employee Id" name="emp_id" required/>
                      </div>      
                      <div className="form-group mb-2">
                          <label htmlFor="emp_id">Enter emp_id <span className="required-field">*</span></label>
                          <input type="text" id="emp_id" value={emp_id} onChange={(e)=>setemp_id(e.target.value)} className="form-control" placeholder="Enter emp_id" name="emp_id" required/>
                      </div>    
                      <div className="form-group">
                          <label htmlFor="select-inventory">Select Type<span className="required-field">*</span></label>
                            <select class="form-control" id="exampleSelect">
                            <option>T1 - Under Rs. 500</option>
                            <option>T2 - Under Rs. 1500</option>
                            <option>T3 - Under Rs. 2500</option>
                            <option>Gift Pack</option>
                            <option>Cash Rewards</option>
                            </select> 
                      </div>     
                    </form>
                    <button type="submit" name="login" id="login" onClick={loginUser} className="mt-3 btn btn-sm w-100 btn-outline-dark">Generate and Send emp_id</button>
                    <button type="submit" name="login" id="login" onClick={loginUser} className="mt-3 btn btn-sm w-100 btn-outline-dark">Upload bulk data</button>
                  </div>
                  <div className="card-footer text-muted">
                 Facing Issue?  <NavLink to="/register">Submit Feedback</NavLink>
                      </div>
                  
                </div>
            </div>
        </div>
    </div>
  );
};

export default GenerateToken;
