import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const EditProfile = () => {
    const navigate = useNavigate();
    const [userData, setUserData] = useState({});
    const calleditProfile = async ()=>{
      console.log("Error Page called");
      try{
        const res = await fetch('/profile',{
          method: 'GET',
          headers : {
            "Accept": 'application/json',
            "ContentType": 'application/json'
          },
          credentials: 'include'
        });
        const data = await res.json();
        console.log(data);
    
        setUserData(data);
        if(!res.status === 200){
          const error = new Error(res.error);
          throw error;
        }
  
      }catch(err){
        console.log(err);
        navigate('/login');
      }
    }
  
    useEffect(() => {
      calleditProfile(); 
    },[]);
  return (
    <>
          <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Edit Profile</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="name">Change Name<span className="required-field">*</span></label>
                          <input type="text" id="name" value={userData.name}  className="form-control" name="name" required/>
                      </div>
                      <div className="form-group mb-2">
                          <label htmlFor="phone">Change Phone<span className="required-field">*</span></label>
                          <input type="text" id="phone" value={userData.phone}  className="form-control" name="phone" required/>
                      </div>     
                     
                    </form>
                    <button type="submit" name="login" id="login"  className="mt-3 btn btn-sm w-100 btn-outline-dark">Continue</button>
                  </div>
                </div>
            </div>
        </div>
    </>
  );
};

export default EditProfile;
