import React, {useState} from "react";
import { NavLink, useNavigate } from "react-router-dom";

const Login = () => {

  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const loginUser = async(e) =>{
    e.preventDefault();
    const res = await fetch("/signin",{
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({email,password})
    });
    const data = await res.json();
    if(res.status === 400 || !data ){
      window.alert(data.error);
      console.log("Some error in Login !");
    }else{ 
      window.alert(data.message);
      console.log("Registration Successfull !");
      navigate("/");
    }
  }


  return (
    <>
          <div className="d-flex justify-content-center align-items-center mt-5">
            <div className="col-4">
                <div className="card h-100 justify-content-center">
                  <h5 className="card-header">Login for Ecovia Store</h5>
                  <div className="card-body d-flex flex-column">
                    <form method="POST">
                      <div className="form-group mb-2">
                          <label htmlFor="email">Email<span className="required-field">*</span></label>
                          <input type="text" id="email" value={email} onChange={(e)=>setEmail(e.target.value)} className="form-control" placeholder="Enter Email" name="email" required/>
                      </div>      
                      <div className="form-group">
                          <label htmlFor="password">Password<span className="required-field">*</span></label>
                          <input type="password" id="password" value={password} onChange={(e)=>setPassword(e.target.value)}  className="form-control" placeholder="Enter Password" name="password" required/> 
                      </div>   
                    </form>
                    <button type="submit" name="login" id="login" onClick={loginUser} className="mt-3 btn btn-sm w-100 btn-outline-dark">Login</button>
                  </div>
                  <div className="card-footer text-muted">
                  Don't have an account? <NavLink to="/register">Register Now</NavLink>
                      </div>
                </div>
            </div>
        </div>
    </>
  );
};

export default Login;
