import React from 'react';
import Home from './components/Home';
import Navbar from './components/Navbar';
import {Route, Routes} from 'react-router-dom';  
import Login from './components/Login';
import Register from './components/Register';
import Profile from './components/Profile';
import Logout from './components/Logout';
import './App.css';
import ErrorPage from './components/ErrorPage';
import AddInventory from './components/addInventory';
import EditProfile from './components/editProfile';
import ChangePassword from './components/changePassword';
import TransferInventory from './components/transferInventory';
import { useReducer } from 'react';
import {initialState, reducer} from '../src/reducer/UseReducer';
import GenerateToken from './components/Profile';
import AddBag from './components/addBag';
import TrackStatus from './components/trackStatus';
import Feedback from 'react-bootstrap/esm/Feedback';
import SubmitFeedback from './components/SubmitFeedback';
import Footer from './components/Footer';
import Reward from './components/redeemReward';
import Address from './components/address';
export const UserContext = React.createContext();

const App = () => {


  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div>
      <UserContext.Provider value={{state, dispatch}}>
      <Navbar />
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/add-bag" element={<AddBag />} />
        <Route path="/track-status" element={<TrackStatus />} />
        <Route path="/submit-feedback" element={<SubmitFeedback />} />
        <Route path="/generate-token" element={<GenerateToken />} />
        <Route path="/select-address" element={<Address />} />
        <Route path="/logout" element={<Logout />} />
        <Route path="/add-inventory" element={<AddInventory />} />
        <Route path="/redeem-reward" element={<Reward />} />
        <Route path="/edit-profile" element={<EditProfile />} />
        <Route path="/change-password" element={<ChangePassword />} />
        <Route path="/transfer-inventory" element={<TransferInventory />} />
        <Route path="*" element={<ErrorPage />} />
      </Routes>
      <footer className="footer">

      <Footer/>
     </footer>
     </UserContext.Provider> 
    </div>
  );
}

export default App;
